def get(csidl: int, *, type: Type=Type.Current) -&gt; Path:
    if (os.name != "nt"):
        raise RuntimeError("BrokenPath.Windows only makes sense on Windows")
    buffer = ctypes.create_unicode_buffer(ctypes.wintypes.MAX_PATH)
